import sqlite3
from flask import Flask, render_template
app = Flask(__name__)

img_data = ''

@app.route('/')
def index():
  try:
    # Подключаемся к базе данных
    conn = sqlite3.connect('data/modbase_music.db') # бд используется из финальной аттестационной работы
    cursor = conn.cursor()
    # Выполняем запрос к базе данных
    cursor.execute("SELECT * FROM top10artist limit 10")
    #получаем массив данных из бд
    rows = cursor.fetchall()
    return render_template('index.html', rows=rows,img_data = img_data)
  except Exception as e:
    return '<font color="red"><h1> Ошибка: ' + str(e) + '</h1></font>'
  finally:
    conn.close()

if __name__ == '__main__':
  app.run()

